<div class="Layout-content"><div class="Layout-left"><h3>Lista de clientes</h3><?php wp_list_pages('sort_column=menu_order&title_li='); ?></div><div class="Layout-right"><div class="Layout-cuenta"></div><div class="Layout-contenido"><div class="Cuenta"><div class="Cuenta-content"><h2>Cliente: <?php the_title(); ?></h2><div class="Cuenta-top"><div class="Cuenta-left"><p>Dirección: <?php the_field('direccion'); ?></p><p>Telefono: <?php the_field('telefono'); ?></p></div><div class="Cuenta-right"><p class="Can">S/ 50.00</p></div></div><div class="Cuenta-bootom"><div class="Cuenta-campos"><div class="Cuenta-titles"><h3 class="Fecha">Fecha</h3><h3 class="Des">Descripcion</h3><h3 class="Pre">Precio</h3></div><div class="Cuenta-conseptos"><?php $idvalue = get_the_ID(); ?>
<?php $args = array('numberposts' => -1, 'post_type'   => 'post', 'meta_key'    => 'id_page', 'meta_value'  => $idvalue); ?>
<?php $the_query = new WP_Query( $args ); ?>
<?php if ( $the_query->have_posts() ) : while ( $the_query->have_posts() ) : $the_query->the_post(); ?><div class="Cuenta-cargos"><span class="Fecha"><?php the_field('fecha_page'); ?></span><span class="Link"></span><span class="Des"><?php the_field('des_page'); ?></span><span class="<?php if (get_field('sumar_page') == '+') { echo 'verde'; } else { echo 'rojo'; } ?>">S/ <?php the_field('sumar_page'); ?> <?php the_field('can_page'); ?></span><span><a href="<?php echo get_delete_post_link(); ?> ">Borrar</a></span></div><?php endwhile;  endif; ?></div></div><form action="" id="primaryPostForm" method="post"><div class="Cuenta-input"><div class="Cuenta-des"><label>Descripcion</label><input type="text" id="des_page" name="des_page" value=""/></div><div class="Cuenta-num"><label>Precio</label><input type="text" id="can_page" name="can_page" value=""/></div></div><div><input type="hidden" id="id_page" name="id_page" value="<?php echo $idvalue; ?>"/><input type="hidden" id="fecha_page" name="fecha_page" value="<?php echo date('j F, Y'); ?>"/><input type="hidden" id="postTitle" name="postTitle" value="<?php the_title(); ?>"/></div><div class="Cuenta-radio"><p><label><input type="radio" id="sumar_page" name="sumar_page" value="+"/><span>Sumar</span></label></p><p><label><input type="radio" id="sumar_page" name="sumar_page" value="-"/><span>Restar</span></label></p><?php wp_nonce_field('post_nonce', 'post_nonce_field'); ?><input type="hidden" id="submitted" name="submitted" value="true"/><button class="btn-large red" type="submit">Enviar</button></div></form></div></div></div><footer class="Layout-footer">Diseñado y desarrollado por Daniel Acevedo</footer></div></div></div>