<div class="Layout-content"><div class="Layout-left"><h3>Lista de clientes</h3><?php wp_list_pages('sort_column=menu_order&title_li='); ?></div><div class="Layout-right"><div class="Layout-cuenta"></div><div class="Layout-contenido"><div class="Cuenta"><div class="Cuenta-content"><h2>Ventas a credito.</h2><div class="Cuenta-top"><div class="Cuenta-left"><p></p></div><div class="Cuenta-right"><p class="Can" id="spTotal"></p><a class="btn-small blue waves-effect waves-light" href="#!" onclick="Sumar();">Total</a></div></div><div class="Cuenta-bootom"><div class="Cuenta-campos"><div class="Cuenta-titles"><h3 class="Fecha">Fecha</h3><h3 class="Cli">Cliente</h3><h3 class="Des">Descripcion</h3><h3 class="Pre">Precio</h3></div><div class="Cuenta-conseptos"><?php if ( have_posts() ) : while ( have_posts() ) : the_post(); ?><div class="Cuenta-cargos"><span class="Fecha"><?php the_field('fecha_page'); ?></span><span class="Cli"><?php the_title(); ?></span><span class="Des"><?php the_field('des_page'); ?></span><span class="Bor"><a href="<?php echo get_delete_post_link(); ?> " title="Click para Borrar">-</a></span><span class="Pre <?php if (get_field('sumar_page') == '+') { echo 'verde'; } else { echo 'rojo'; } ?>">S/<span><?php the_field('sumar_page'); ?><input class="<?php if (get_field('sumar_page') == '+') { echo 'verde'; } else { echo 'rojo'; } ?>" id="<?php the_field('ram_page'); ?>" type="text" value="<?php the_field('can_page'); ?>" disabled="disabled"/></span></span></div><?php endwhile;  endif; ?></div></div></div></div></div><?php include('footer.php'); ?><script type="text/javascript">function Sumar() {<?php if ( have_posts() ) : while ( have_posts() ) : the_post(); ?><?php the_field('ram_page'); ?> = document.getElementById('<?php the_field('ram_page'); ?>').value;<?php endwhile;  endif; ?>suma =<?php if ( have_posts() ) : while ( have_posts() ) : the_post(); ?><?php the_field('sumar_page'); ?> parseFloat(<?php the_field('ram_page'); ?>)<?php endwhile;  endif; ?>;document.getElementById('spTotal').innerHTML = "S/ "+suma;}</script></div></div></div>